  client.CacheDuration = 3600; // 60 секунд * 60 минут = 3 600 секунд = 1 час
  client.CacheType = CachingType.File;
  // формирование запроса
  client.CommandText = "SELECT * FROM forum_messages";
  // выполнение запроса
  var table = client.GetTable();
